import { IMOutPresenter, PresentData } from "./IGameDriver";
import { WordleGameResult } from "./WordleGameResult";
import { WordleLetter } from "./WordleLetter";
import { WordleLine } from "./WordleLine";
import { WordleState } from "./wordleState";

export class WordleFakePresenter implements IMOutPresenter<WordleGameResult> {
  _result: WordleGameResult = {
    data: [],
    motATrouver: "",
    nombreEssais: 0,
    actualEssais: 0
  };

  _resultToDisplay: WordleGameResult = {
    data: [],
    motATrouver: "",
    nombreEssais: 0,
    actualEssais: 0
  };

  view(): PresentData<WordleGameResult> {
    this.assignGrid();
    this.assignOtherResult();

    return { data: this._resultToDisplay, error: "" };
  }

  private assignGrid() {
    if (this._result.actualEssais < 1) {
      this._resultToDisplay.data = createStartGille(this._result);
    } else {
      const index = this._result.actualEssais - 1;
      this._resultToDisplay.data[index] = this._result.data[index];
    }
  }

  private assignOtherResult() {
    this._resultToDisplay.actualEssais = this._result.actualEssais;
    this._resultToDisplay.motATrouver = this._result.motATrouver;
    this._resultToDisplay.nombreEssais = this._result.nombreEssais;
  }

  public presentData(result: WordleGameResult): void {
    this._result = result;
  }
}

function createStartWordleLine(lengthOfWord: number): WordleLetter[] {
  const line = [];
  for (let i = 0; i < lengthOfWord; i++) {
    line.push(new WordleLetter("?", WordleState.NoLettter));
  }
  return line;
}

function createStartGille(result: WordleGameResult): WordleLine[] {
  const newGrille: WordleLine[] = [];
  for (let i = 0; i < result.nombreEssais; i++) {
    newGrille[i] = new WordleLine(
      createStartWordleLine(result.motATrouver.length)
    );
  }
  return newGrille;
}

import { WordleLine } from "./WordleLine";

export class WordleGameResult {
  public data: WordleLine[] = [];
  public motATrouver = "";
  public nombreEssais = 5;
  public actualEssais = 0;
}

import { Inject } from "@angular/core";
import { WordleLine } from "./WordleLine";
import { IGameDriver } from "./IGameDriver";
import { WordleGameResult } from "./WordleGameResult";

const NOMBRE_ESSAIS = 5;
export class WordleGameViewModel {
  constructor(
    @Inject("IGameProvider")
    private _gameProvider: IGameDriver<WordleGameResult>
  ) {}
  //private _grille: WordleLine[] = [];
  private _status = GameStatus.Init;
  public nombreEssais = 5;

  private _result: WordleGameResult = {
    actualEssais: 0,
    motATrouver: "",
    data: [],
    nombreEssais: this.nombreEssais
  };

  get grille(): WordleLine[] {
    return this._result.data;
  }

  get isInit() {
    return this._status === GameStatus.Init;
  }

  get isStarted() {
    return this._status === GameStatus.Start;
  }

  get isWin() {
    return this._status === GameStatus.Win;
  }

  get isLoose() {
    return this._status === GameStatus.Loose;
  }

  initGame(motATrouver: string): void {
    this._result = this._gameProvider.createGame(
      motATrouver,
      this.nombreEssais
    );
    this._status = GameStatus.Start;
  }

  propose(proposeWord: string) {
    this._result = this._gameProvider.propose(proposeWord);
    this.testIfWinOrLoose();
  }

  private testIfWinOrLoose() {
    if (this._result.data.some((x) => x.isGoodWord())) {
      this._status = GameStatus.Win;
    }
    if (
      this._result.actualEssais === this._result.nombreEssais &&
      this._status != GameStatus.Win
    ) {
      this._status = GameStatus.Loose;
    }
  }

  restart() {
    this._result = this._gameProvider.restart();
    this._status = GameStatus.Init;
  }
}

export enum GameStatus {
  Init = 0,
  Start = 1,
  Win = 2,
  Loose = 3
}

import { WordleState } from "./wordleState";

export class WordleLetter {
  constructor(
    public value: string,
    public state: WordleState
  ) {
    this.value = value;
    this.state = state;
  }
}

import { WordleLetter } from "./WordleLetter";
import { WordleState } from "./wordleState";

export class WordleLine {
  constructor(private _letters: WordleLetter[] = []) {}

  public setLetters(value: WordleLetter[]) {
    this._letters = value;
  }

  public get letters(): WordleLetter[] {
    return this._letters;
  }

  public isGoodWord(): boolean {
    if (this._letters)
      return this._letters.every((x) => x.state == WordleState.good);

    return false;
  }
}

